import React, {Component} from 'react';
import {Map, GoogleApiWrapper,  InfoWindow, Marker} from 'google-maps-react';

// NA 54.5260, 105.2551
// SA 8.7832, 55.4915
// OC 22.7359, 140.0188
// AS 34.0479, 100.6197
// EU 54.5260, 15.2551
//AN 82.8628, 135.0000



const mapStyles = {
    width: '100%',
    height: '90%'
  };

  const continents = {
    NA: {lat: 54.5260, lng: -105.2551},
    SA: {lat: -5, lng: -55.4915},
    AF: {lat: 34.5085, lng: 8.7832},
    OC: {lat: 22.7359, lng: 140.0188},
    AS: {lat: 34.0479, lng: 100.6197},
    EU: {lat: 54.5260, lng: 15.2551},
    
  }

  export class MapContainer extends Component {
      state = {
        showingInfoWindow: false,
        activeMarker: {},
        selectedPlace: {}
      };
      onClose = props => {
        if(this.state.showingInfoWindow) {
            this.setState({
                showingInfoWindow: false,
                activeMarker: null
                });
            }
        };
    onMarkerClick = (props, marker, e) =>
        this.setState({
            selectedPlace: props,
            activeMarker: marker,
            showingInfoWindow: true
        });
         
      render() {
          return(
              
              <Map
              google={this.props.google}
            //   zoom={9}
            zoom = {
                this.props.center
                ? 4
                : 9
            }
              style={mapStyles}
              initialCenter={{lat: 44.3, lng: -76.48}}
              center={
                  this.props.center
                  ? continents[this.props.center]
                  : {lat: 44.3, lng: -76.48}
                }
            >
                <Marker
                    onClick={this.onMarkerClick}
                    name={'Kenyatta'}
                    position={{ lat: 44.230533, lng: -76.460828}}

                />
                <InfoWindow
                    marker={this.state.activeMarker}
                    visible={this.state.showingInfoWindow}
                    onClose={this.onClose}
                >
                    <div>
                        <h4>{this.state.selectedPlace.name}</h4>
                    </div>
                </InfoWindow>
            </Map>
            );
      }
  }

// function onMarkerClick(props, marker, e) {
//     this.setState({
//         selectedPlace: props,
//         activeMarker: marker,
//         showingInfoWindow: true
//     });
// }

// function onClose(props) {
//     if(this.state.showingInfoWindow) {
//         this.setState({
//             showingInfoWindow: false,
//             activeMarker: null
//         });
//     }
// } 

export default GoogleApiWrapper({
    apiKey: 'AIzaSyCp1Wpapl48sNfmuNfOx-RRJydW4gMGaRI'
})(MapContainer);
