import React, {Component} from 'react';
import Button from '@material-ui/core/Button';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';

const [anchorEl, setAnchorEl] = React.useState(null);
  function handleClick(event) {
    setAnchorEl(event.currentTarget);
  }
  function handleClose() {
    setAnchorEl(null);
  }

export class dropMenu extends Component {
    // default function SimpleMenu() {
//   



changeView = (continent) => {
    return (
      this.setState({
        center: continent
      })
    )
  }

  render() {
  return (
    <div>
      <Button aria-controls="simple-menu" aria-haspopup="true" onClick={handleClick}>
        Continents 
      </Button>
      <Menu
        id="continents-menu"
        anchorEl={anchorEl}
        keepMounted
        open={Boolean(anchorEl)}
        onClose={handleClose}
      >
        <MenuItem onClick={() => this.changeView("OC")}>Oceania</MenuItem>
        <MenuItem onClick={handleClose}>Asia</MenuItem>
        <MenuItem onClick={handleClose}>South America</MenuItem>
        <MenuItem onClick={handleClose}>Africa</MenuItem>
        <MenuItem onClick={handleClose}>Europe</MenuItem>
        <MenuItem onClick={handleClose}>North America</MenuItem>
      </Menu>
    </div>
  );
}
};
export default dropMenu;
