import React, {Component} from 'react';
import Button from '@material-ui/core/Button';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';


//   function handleClick(event) {
//     const [anchorEl, setAnchorEl] = React.useState(null);
//     setAnchorEl(event.currentTarget);
//   }
//   function () => handleClose() {
//     setAnchorEl(null);
//   }

export class dropMenu extends Component {
    // default function SimpleMenu() {
//   



changeView = (continent) => {
    return (
      this.setState({
        center: continent
      })
    )
  }

  render() {
  return (
    <div>
      <Button aria-controls="simple-menu" aria-haspopup="true" //onClick={handleClick}
      >
        Continents 
      </Button>
      <Menu
        id="continents-menu"
        // anchorEl={anchorEl}
        // keepMounted
        // open={Boolean(anchorEl)}
        // onClose={handleClose}
      >
        <MenuItem onClick={() => this.changeView("OC")}>Oceania</MenuItem>
        <MenuItem onClick={() => this.changeView("OC")}>Asia</MenuItem>
        <MenuItem onClick={() => this.changeView("OC")}>South America</MenuItem>
        <MenuItem onClick={() => this.changeView("OC")}>Africa</MenuItem>
        <MenuItem onClick={() => this.changeView("OC")}>Europe</MenuItem>
        <MenuItem onClick={() => this.changeView("OC")}>North America</MenuItem>
      </Menu>
    </div>
  );
}
};
export default dropMenu;
