import React, {Component} from 'react';
import MapContainer from './components/map';
import styled, {css} from 'styled-components'
import SimpleMenu, { dropMenu } from './components/dropmenu';
import ContinentMenu from './components/continentsMenu';
import Dropdown from 'react-bootstrap/Dropdown';
import DropdownButton from 'react-bootstrap/DropdownButton';

const Button = styled.button`
  background: transparent;
  border-radius: 3px;
  border: 2px solid red;
  color: red;
  margin: 0 1em;
  padding: 0.25em 1em;

  ${props =>
    props.primary &&
    css`
      background: red;
      color: white;
    `};
`
export class App extends Component {
  state = {
    center: undefined
  };

  changeView = (continent) => {
    return (
      this.setState({
        center: continent
      })
    )
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
        <h2>
          <Button primary onClick={() => this.changeView("NA")}> North America </Button>
          <Button primary onClick={() => this.changeView("SA")}> South America </Button>
          <Button primary onClick={() => this.changeView("OC")}> Oceania </Button>
          <Button primary onClick={() => this.changeView("AS")}> Asia </Button>
          <Button primary onClick={() => this.changeView("EU")}> Europe </Button>
          <Button primary onClick={() => this.changeView("AF")}> Africa </Button>
          <Dropdown>
            <Dropdown.Toggle variant="success" id="dropdown-basic">
              Dropdown Button
            </Dropdown.Toggle>

            <Dropdown.Menu>
              <Dropdown.Item href="#/action-1">Action</Dropdown.Item>
              <Dropdown.Item href="#/action-2">Another action</Dropdown.Item>
              <Dropdown.Item href="#/action-3">Something else</Dropdown.Item>
            </Dropdown.Menu>
          </Dropdown>

          <DropdownButton id="dropdown-item-button" title="Dropdown button">
            <Dropdown.Item as="Button">Action</Dropdown.Item>
            <Dropdown.Item as="button">Another action</Dropdown.Item>
            <Dropdown.Item as="button">Something else</Dropdown.Item>
          </DropdownButton>

          <SimpleMenu changeView={this.changeView} />
          <ContinentMenu/>
         </h2>
        </header>
        <MapContainer center={this.state.center} />

      </div>
    );
  }

}
export default App;
